dictionary = {}
characters = ".@" + string.digits + string.ascii_uppercase
for ch in characters:
    dictionary[ch] = []

for address in addresses_lang:
    print("-------")
    with requests.Session() as s:
        result = s.get(address)
        soup = BeautifulSoup(result.content)
        for link in soup.find_all("a"):
            for ch in characters:
                if link.get("href") is not None and link.get("href").startswith("/wiki/{}".format(ch)):
                    if link.get("title") not in dictionary[ch]:
                        dictionary[ch].append(link.get("title"))

for address in addresses_po:
    print("-------")
    with requests.Session() as s:
        result = s.get(address)
        soup = BeautifulSoup(result.content)
        for link in soup.find_all("a"):
            for ch in characters:
                if link.get("href") is not None and link.get("href").startswith("/wiki/{}".format(ch)):
                    if link.get("title") not in dictionary[ch]:
                        dictionary[ch].append(link.get("title"))

num = 0
for k in dictionary:
    print(k, dictionary[k])
    num += len(dictionary[k])

print(num)


with open("data/skill_dictionary", "w") as writer:
    writer.write(json.dumps(dictionary))


